n            temp_node = temp_node->left_child;\n            if (temp_node == NULL)\n                break;\n            nque.push(temp_node);\n            temp_node = nque.front()->right_brother;\n            delete nque.front();\n            nque.pop();\n            if (temp_node == NULL)\n                break;\n            nque.push(temp_node);\n            temp_node = nque.front();\n            nque.pop();\n        }\n        while (!nque.empty())\n        {\n            delete nque.front();\n            nque.pop();\n        }\n        cout << endl;\n        temp_root = temp_root->right_brother;\n        delete temp_root;\n    }\n}\n \nint Bin_heap::minimum()\n{\n    if (head == NULL)\n        return 0;\n    Node *temp_root = head;\n    int min = temp_root->key;\n    while (temp_root->right_brother != NULL)\n    {\n        if (temp_root->right_brother->key < min)\n            min = temp_root->right_brother->key;\n        temp_root = temp_root->right_brother;\n    }\n    return min;\n}\n \nBin_heap::Node::Node(int elem) : left_child(NULL), right_brother(NULL), parent(NULL)\n{\n    key = elem;\n    degree = 0;\n}\n \nvoid Bin_heap::insert(int elem)\n{\n    Bin_heap *heap = new Bin_heap(elem);\n    union_heap (heap);\n}\n \nvoid Bin_heap::link_heap(Bin_heap::Node *root1, Bin_heap::Node *root2)\n{\n    if (root1->left_child != NULL)\n        root1->left_child->parent = root2;\n    root1->right_brother = root2->right_brother;\n    root2->right_brother = root1->left_child;\n    root1->left_child = root2;\n    root2->parent = root1;\n    root1->degree++;\n}\n \nvoid Bin_heap::union_heap(Bin_heap *heap)\n{\n    if (head == NULL)\n    {\n        head = heap->head;\n        return;\n    }\n    Node *temp_root = head;\n    while (temp_root->right_brother != NULL)\n        temp_root = temp_root->right_brother;\n    temp_root->right_brother = heap->head;\n    sort_by_degree();\n    union_heap_samedegree();\n}\n \nvoid Bin_heap::union_heap_samedegree()\n{\n    Node *temp_root = head;\n    bool linked = false;\n    while (temp_root->right_brother != NULL)\n    {\n        if (temp_root->degree == temp_root->right_brother->degree)\n        {\n            link_heap (temp_root, temp_root->right_brother);\n            sort_by_degree();\n            linked = true;\n        }\n        else\n            temp_root = temp_root->right_brother;\n    }\n    if (linked)\n        union_heap_samedegree();\n}\n \nvoid Bin_heap::sort_by_degree()\n{\n    Node *temp_node = head;\n    vector<Node *> nvec;\n    while (temp_node != NULL)\n    {\n        nvec.push_back(temp_node);\n        temp_node = temp_node->right_brother;\n    }\n    if (nvec.size() <= 1)\n        return;\n    sort (nvec, 0, nvec.size());\n    head = nvec[0];\n    temp_node = head;\n    for (size_t i=1; i<nvec.size(); ++i)\n        nvec[i-1]->right_brother = nvec[i];\n    nvec[nvec.size()-1]->right_brother = NULL;\n}\n \nvoid Bin_heap::sort (vector<Bin_heap::Node *> &nvec, size_t beg, size_t end)\n{\n    if (beg < 0 || end > nvec.size() || beg+1 >= end)\n        return;\n    int posi = beg;\n    for (size_t posj=beg+1; posj<end; ++posj)\n    {\n        if (nvec[posj]->degree < nvec[beg]->degree\n                || (nvec[posj]->degree == nvec[beg]->degree\n                    && nvec[posj]->key < nvec[beg]->key))\n        {\n            posi++;\n            exch (nvec, posi, posj);\n        }\n    }\n    exch (nvec, posi, beg);\n    sort (nvec, beg, posi);\n    sort (nvec, posi+1, end);\n}\n \nvoid Bin_heap::exch (vector<Bin_heap::Node *> &nvec, size_t posi, size_t posj)\n{\n    if (posi == posj)\n        return;\n    Node *tmp = nvec[posi];\n    nvec[posi] = nvec[posj];\n    nvec[posj] = tmp;\n}\n \nvoid Bin_heap::print_heap()\n{\n    Node *temp_root = head;\n    while (temp_root != NULL)\n    {\n        cout << temp_root->degree << \'-\' << temp_root->key << \' \';\n        Node *temp_node = temp_root->left_child;\n        queue<Node *> nque;\n        if (temp_node != NULL)\n        {\n 